mapper.select:
SELECT COUNT(*) AS COUNT
FROM GALLERY
WHERE ID = #{gallery_id}
  AND PARTICIPANT_ID = #{participant_id}

SELECT UPLOAD_DATE
FROM GALLERY
WHERE ID = #{gallery_id}
  AND PARTICIPANT_ID = #{participant_id}

SELECT COUNT(*) AS COUNT
FROM GL_COMMENT
WHERE GALLERY_ID = #{gallery_id}

SELECT ROOT, FILE_SETTINGNAME
FROM GALLERY
WHERE ID = #{gallery_id}
